"use strict";class DetailsProduct{constructor(e,t,d){this.name=e,this.price=t,this.year=d}}class UI{addProduct(e){const t=document.getElementById("index-section-products"),d=document.createElement("article");d.className="product-card",d.innerHTML='\n\t\t<p class="product-card__info">\n\t\t\t<b>Product: </b>'.concat(e.name,"\n\t\t\t<b>Price: </b>").concat(e.price,"\n\t\t\t<b>Year: </b>").concat(e.year,'\n\t\t</p>\n\t\t<input type="submit" value="Delete" name="delete" class="btn-delete">\n        '),t.appendChild(d)}resetForm(){document.getElementById("card-add__form").reset()}deleteProduct(e){"delete"===e.name&&(e.parentElement.remove(),this.showMessage("Product Deleted Successfully","deleted"))}showMessage(e,t){const d=document.createElement("div");d.className="message-product message-product--".concat(t),d.appendChild(document.createTextNode(e));document.querySelector(".message-products-container").appendChild(d),setTimeout((()=>{document.querySelector(".message-product").remove()}),2e3)}}document.getElementById("card-add__form").addEventListener("submit",(e=>{e.preventDefault();const t=document.getElementById("product-name").value,d=document.getElementById("product-price").value,c=document.getElementById("product-year").value,n=new DetailsProduct(t,d,c),r=new UI;if(""===t||""===d||""===c)return r.showMessage("Please Insert data in all fields","danger");r.addProduct(n),r.showMessage("Product Added Successfully","added"),r.resetForm()})),document.getElementById("index-section-products").addEventListener("click",(e=>{(new UI).deleteProduct(e.target),e.preventDefault()}));